mapper.insert:
INSERT INTO site_link_data(
	  site_srl
	, link_srl
	, data_id
	, data_title
	, data_link
	, data_img
	, data_content
) VALUES (
	  #{siteSrl}
	, #{linkSrl}
	, #{dataId}
	, #{dataTitle}
	, #{dataLink}
	, #{dataImg}
	, #{dataContent}
)

mapper.update:
UPDATE site_link_data SET
	 data_title = #{dataTitle}
	, data_link = #{dataLink}
	, data_img = #{dataImg}
	, data_content = #{dataContent}
	, upt_dt = SYSDATE()
WHERE 	site_srl = #{siteSrl}
AND site_srl = #{linkSrl}
AND data_id = #{dataId}
